mapper.select:
SELECT id, register,damage_type,register_time,confirmed_status,confirmer,duty_officer,damage_detail,damage_picture_path FROM tb_damage
WHERE vehicle_id = #{vehicleId,jdbcType=INTEGER}
ORDER BY create_time DESC

mapper.update:

update tb_damage
set damage_picture_path = #{damagePicturePath,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}


update tb_damage
set confirmer = #{confirmer,jdbcType=VARCHAR},confirmed_status = #{confirmedStatus,jdbcType=INTEGER}
where vehicle_id = #{vehicleId,jdbcType=INTEGER} AND damage_type =  #{damageType,jdbcType=INTEGER}


update tb_damage
set damage_detail = #{damageDetail,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}
