mapper.select:

select 

from tb_original_brand
where brandName = #{brandname,jdbcType=VARCHAR}
  and modelYear = #{modelyear,jdbcType=VARCHAR}
  and subModelName = #{submodelname,jdbcType=VARCHAR}
  and versionName = #{versionname,jdbcType=VARCHAR}
limit 0,1

mapper.delete:

delete from tb_original_brand
where brandName = #{brandname,jdbcType=VARCHAR}
  and modelYear = #{modelyear,jdbcType=VARCHAR}
  and subModelName = #{submodelname,jdbcType=VARCHAR}
  and versionName = #{versionname,jdbcType=VARCHAR}
